import {v1} from 'uuid';


export type MessageType = {
    id: string
    message: string
    likesCount: number
}

export type DialogsType = {
    id: string
    name: string
}

export type PostDataType = {
    id: string
    message: string
    likesCount: number
}

export type MessagePageType = {
    messagesData: Array<MessageType>
}

export type DialogsPageType = {
    dialogsData: Array<DialogsType>
}

export type ProfilePageType = {
    postsData: Array<PostDataType>
    newPostText: string
}

export type StateType = {
    profilePage: ProfilePageType
    dialogsPage: DialogsPageType
    messagePage: MessagePageType
}

export type AddPostActionType = {
    type: 'ADD-POST'
}

export type UpdateNewPostTextActionType = {
    type: 'UPDATE-NEW-POST-TEXT'
    newText: string
}

export type ActionsType = {

}


export type StoreType = {
    _state: StateType
    /*updateNewPostText: (newText: string) => void
    addPost: () => void*/
    callSubscriber: () => void
    subscribe: (abserver: () => void) => void
    getState: () => StateType
    dispatch: (action: AddPostActionType | UpdateNewPostTextActionType) => void
}

export const store: StoreType = {
    _state: {
        profilePage: {
            postsData: [
                {id: v1(), message: 'Hello my friend!', likesCount: 22},
                {id: v1(), message: 'Hello samurai!', likesCount: 12},
                {id: v1(), message: 'You are the best!', likesCount: 24},
                {id: v1(), message: 'Good night!', likesCount: 15},
            ],
            newPostText: 'it-kamasutra.com'
        },
        dialogsPage: {
            dialogsData: [
                {id: v1(), name: 'Dimych'},
                {id: v1(), name: 'Sveta'},
                {id: v1(), name: 'Valera'},
                {id: v1(), name: 'Victor'},
                {id: v1(), name: 'Sasha'},
                {id: v1(), name: 'Andrey'},
                {id: v1(), name: 'Igor'},
            ]
        },
        messagePage: {
            messagesData: [
                {id: v1(), message: 'Hello', likesCount: 22},
                {id: v1(), message: 'How is your learning?', likesCount: 36},
                {id: v1(), message: 'Yo', likesCount: 23},
                {id: v1(), message: 'You good!', likesCount: 55},
                {id: v1(), message: 'My name is Mike', likesCount: 12},
            ]
        }

    },
    /*updateNewPostText(newText: string) {
        this._state.profilePage.newPostText = newText
        this.callSubscriber()
    },
    addPost() {
        let newPost: PostDataType = {
            id: v1(),
            message: this._state.profilePage.newPostText,
            likesCount: 0
        }
        this._state.profilePage.postsData.push(newPost)
        this._state.profilePage.newPostText = ''
        this.callSubscriber()
    },*/
    callSubscriber() {
        console.log('State chenged')
    },
    subscribe(abserver) {
        this.callSubscriber = abserver
    },
    getState() {
        return this._state
    },

    dispatch(action) {
        if (action.type === 'ADD-POST') {
            const newPost: PostDataType = {
                id: v1(),
                message: this._state.profilePage.newPostText,
                likesCount: 0
            }
            this._state.profilePage.postsData.push(newPost)
            this._state.profilePage.newPostText = '';
            this.callSubscriber();
        } else if (action.type === 'UPDATE-NEW-POST-TEXT') {
            if (action.newText != null) {
                this._state.profilePage.newPostText = action.newText;
            }
            this.callSubscriber()
        }
    }
}
